import React, { Component } from 'react';
import Content from "./components/Content";
import TOC from './components/TOC';
import Subject from "./components/Subject";
import './App.css';



class App extends Component {
  constructor(props){
    super(props);
    this.state= {
      mode:'welcome',
      subject:{title:'하이 hi', sub:'우린 매드 몬스터',  photo:"//file.namu.moe/file/1e6af05025f45ece767276cfce4500b1da951b855d1cf525369b39994ecfc18551b61452cab09932b9abf9ccf426020d"},
      welcome:{title:'MAD ENTERTAINMENT'},
      contents:[
      {id:1, title:'탄', desc:'탄이야', photo:"//w.namu.la/s/5508a6864f0f6393ced09ff8f74ecdf00623136dd00530683802a66a4c855b4007504e854522453a70daeeba6be7c5b81236cef3debe1ea20d0697a8c40dc8f35ecec30d8268aee020ee427dd1e94fbff312eaff14e0cb6dd2c53551eed06f0e0fe653678e5d60a24170488c71133d1c" },
      {id:2, title:'제이호' , desc:'제이호야', photo:"//w.namu.la/s/f28395c94457d660b44b8312b79d19a8e24996e3a92f2c2cd713661282a27d923b68bb294a047ecfdba40c7e6b2cb73af3fcdab51dada50545174423bd48b1b4720150e0a07a20b5a42d646afefd7cf5a8e30a4089d8389aa9b1c2991bd77a2d"}]
    }
  }
  render() {
    var _title, _desc, _photo = null;
    if(this.state.mode === 'welcome'){
        _title = this.state.welcome.title;
        _photo = this.state.welcome.photo;
    }
    else if (this.state.mode === 'read'){
      var i = 0;
      while(i < this.state.contents.length){
        var data = this.state.contents[i];
        if(data.id === this.state.selected_content_id ){
          _title = data.title;
          _desc = data.desc;
          _photo = data.photo;
          break;
        }
        i = i + 1;
      }

    }
    return(
      <div className="App">
         <Subject 
         title = {this.state.subject.title} 
         sub = {this.state.subject.sub}
         onChangePage = {function(){
           this.setstate({
             mode:'welcome',
            });

         }.bind(this)}
         ></Subject>
         <TOC 
         onChangePage={function(id){
           this.setState({
             mode:'read',
             selected_content_id:Number(id)
          });
         }.bind(this)} 
         data = {this.state.contents}></TOC>
         <Content title={_title} desc={_desc} photo ={_photo}></Content>
      </div>
    )
  }
}


export default App;
